# Mean Squared Error computation
def mse(matrix1, matrix2):
    """Compute Mean Squared Error between two matrices."""
    return np.mean((matrix1 - matrix2) ** 2)

# Function to resize and compare square pattern images
def resize_and_compare_square(java_downscaled, java_reverted, analy_degree, synthe_degree, interp_degree):
    # Create a 10x10 square image
    input_img = np.zeros((10, 10))
    input_img[3:7, 3:7] = 255.0  # White square in the center
    input_img_normalized = input_img / 255.0

    zoom_factor = 0.5
    output_shape = (int(round(input_img.shape[0] * zoom_factor)), int(round(input_img.shape[1] * zoom_factor)))
    downscaled_img = np.zeros(output_shape, dtype=np.float64)
    reverted_shape = (10, 10)
    reverted_img = np.zeros(reverted_shape, dtype=np.float64)

    # Resize and revert
    resizer = Resize()
    resizer.compute_zoom(input_img=input_img_normalized, output_img=downscaled_img, analy_degree=analy_degree,
                         synthe_degree=synthe_degree, interp_degree=interp_degree, zoom_y=zoom_factor, zoom_x=zoom_factor, shift_y=0.0, shift_x=0.0, inversable=False)
    reverse_zoom = 1.0 / zoom_factor
    resizer.compute_zoom(input_img=downscaled_img, output_img=reverted_img, analy_degree=analy_degree,
                         synthe_degree=synthe_degree, interp_degree=interp_degree, zoom_y=reverse_zoom, zoom_x=reverse_zoom, shift_y=0.0, shift_x=0.0, inversable=False)

    # Compute differences and MSE
    downscaled_diff = downscaled_img - java_downscaled
    reverted_diff = reverted_img - java_reverted
    downscaled_mse = mse(downscaled_img, java_downscaled)
    reverted_mse = mse(reverted_img, java_reverted)

    print(f"Square - Degrees (Analy: {analy_degree}, Synthe: {synthe_degree}, Interp: {interp_degree})")
    print("\nDownscaled Difference:\n", downscaled_diff)
    print(f"\nDownscaled MSE: {downscaled_mse}\n")
    print("Reverted Difference:\n", reverted_diff)
    print(f"\nReverted MSE: {reverted_mse}")
    print("\n" + "="*80 + "\n")

# Function to resize and compare sinusoid pattern images
def resize_and_compare_sinusoid(java_downscaled, java_reverted, analy_degree, synthe_degree, interp_degree):
    # Create a 5x5 image with a sinusoidal pattern overlay on a central white square
    input_img = np.zeros((5, 5))
    freq_x, freq_y = 2.0 * np.pi / 5.0, 3.0 * np.pi / 5.0
    for x in range(5):
        for y in range(5):
            sinusoid = 0.5 * (np.sin(freq_x * x) + np.cos(freq_y * y))
            input_img[x, y] = 1.0 + sinusoid if 1 <= x < 4 and 1 <= y < 4 else sinusoid
    input_img_normalized = input_img / (1.0 + np.max(input_img))

    zoom_factor = 2.0
    output_shape = (int(round(input_img.shape[0] * zoom_factor)), int(round(input_img.shape[1] * zoom_factor)))
    upscaled_img = np.zeros(output_shape, dtype=np.float64)
    reverted_shape = (5, 5)
    reverted_img = np.zeros(reverted_shape, dtype=np.float64)

    # Resize and revert
    resizer = Resize()
    resizer.compute_zoom(input_img=input_img_normalized, output_img=upscaled_img, analy_degree=analy_degree,
                         synthe_degree=synthe_degree, interp_degree=interp_degree, zoom_y=zoom_factor, zoom_x=zoom_factor, shift_y=0.0, shift_x=0.0, inversable=False)
    reverse_zoom = 1.0 / zoom_factor
    resizer.compute_zoom(input_img=upscaled_img, output_img=reverted_img, analy_degree=analy_degree,
                         synthe_degree=synthe_degree, interp_degree=interp_degree, zoom_y=reverse_zoom, zoom_x=reverse_zoom, shift_y=0.0, shift_x=0.0, inversable=False)

    # Compute differences and MSE
    upscaled_diff = upscaled_img - java_downscaled
    reverted_diff = reverted_img - java_reverted
    upscaled_mse = mse(upscaled_img, java_downscaled)
    reverted_mse = mse(reverted_img, java_reverted)

    print(f"Sinusoid - Degrees (Analy: {analy_degree}, Synthe: {synthe_degree}, Interp: {interp_degree})")
    print("\nUpscaled Difference:\n", upscaled_diff)
    print(f"\nUpscaled MSE: {upscaled_mse}\n")
    print("Reverted Difference:\n", reverted_diff)
    print(f"\nReverted MSE: {reverted_mse}")
    print("\n" + "="*80 + "\n")

